    const { productId } = req.body; // destructuring 
    if (!productId) {
        return responseHandler(res, 400, "Bad request")
    }
    var result =  await productModel.findOne({ countId: productId }, { __v: 0, password: 0, createdAt: 0, updatedAt: 0})
